<?php
$image_path = 'data:image/png;base64,';
$hotline = '#';
$profile = new stdClass;
$profile->status		= 'N/A';
$profile->blood			= 'N/A';
$profile->dob			= 'N/A';
$profile->hotline		= '#';
$thai_id				= '-';
if(isset($detail)){
	if($detail->thai_id!=''){
		$thai_id = str_split($detail->thai_id);
		$thai_id = $thai_id[0]. '-'. $thai_id[1]. $thai_id[2]. $thai_id[3]. $thai_id[4]. '-'. $thai_id[5]. $thai_id[6]. $thai_id[7]. $thai_id[8]. $thai_id[9]. '-'. $thai_id[10]. $thai_id[11]. '-'. $thai_id[12];
	}
	$profile->status		= $status[$detail->status];
	$profile->blood			= $blood[$detail->blood_type];
	$profile->religion		= $religion[$detail->religion];

	if($detail->dob != ''){
		$current_year = DATE('Y');
		$birth_year = DATE('Y', strtotime($detail->dob));

		$profile->dob   = DATE('d/m/', strtotime($detail->dob));
		$profile->dob   .= $birth_year + 543;
		
		$age = $current_year - $birth_year;	
		$profile->dob   .= ' <span class="small">['.$age.' ปี]</span>';
	}

	$hotline = '#';
	if($detail->hotline != ''){
		$hotline = 'tel:'.$detail->hotline;
	}

	if($detail->image != ''){
		$image_path = $root.'assets/uploads/'.$detail->image;
	}
}
